mapper.select:
SELECT cc.CYCLE_NAME AS cycleName , cc.YEAR AS year , cc.QUARTER AS quarter , DATE_FORMAT(cr.CREATED_AT, '%Y-%m-%d') AS createdAt , cr.COMPETENCY_REPORT_ID AS competencyReportId , emp.NAME AS employeeName -- 사원 이름 , emp.EMPLOYEE_NO AS employeeNo -- 사번 , dp.DEPT_NAME AS deptName -- 부서명 , cmp.NAME AS positionName -- 직위명 FROM COMPETENCY_REPORT cr JOIN CYCLE cc ON cc.CYCLE_ID = cr.CYCLE_ID JOIN EMPLOYEE emp ON cr.EMP_ID = emp.EMP_ID JOIN DEPARTMENT dp ON emp.DEPT_ID = dp.DEPT_ID JOIN COMPANY_POSITION cmp ON emp.POSITION_ID = cmp.COM_POSITION_ID WHERE cr.IS_DELETED = 'N' AND emp.TYPE = 'WORKING' -- 재직 중 AND cc.YEAR = #{year} AND cc.QUARTER = #{quarter} AND cc.CYCLE_ID = #{cycleId}
AND dp.DEPT_ID = #{deptId}
AND cmp.COM_POSITION_ID = #{comPositionId}
AND emp.EMPLOYEE_NO LIKE CONCAT('%', #{employeeNo}, '%')
AND emp.NAME LIKE CONCAT('%', #{employeeName}, '%')
ORDER BY cc.YEAR DESC, cc.QUARTER DESC, cr.CREATED_AT DESC

SELECT cc.CYCLE_ID , cc.YEAR , cc.QUARTER , cc.CYCLE_NAME , DATE_FORMAT(cc.START_DATE, '%Y-%m-%d') AS startDate , DATE_FORMAT(cc.END_DATE, '%Y-%m-%d') AS endDate , cc.STATUS , emp.NAME AS empName , cc.IS_COMP_REPORT_GENERATED FROM CYCLE cc JOIN employee emp ON cc.EMP_ID = emp.EMP_ID WHERE cc.IS_DELETED = 'N'
AND cc.YEAR = #{year}
AND cc.QUARTER = #{quarter}
ORDER BY cc.YEAR DESC ,cc.QUARTER DESC

SELECT cc.CYCLE_NAME AS cycleName , cc.YEAR AS year , cc.QUARTER AS quarter , DATE_FORMAT(cr.CREATED_AT, '%Y-%m-%d') AS createdAt , cr.COMPETENCY_REPORT_ID AS competencyReportId , emp.NAME AS employeeName -- 사원 이름 , emp.EMPLOYEE_NO AS employeeNo -- 사번 , dp.DEPT_NAME AS deptName -- 부서명 , cmp.NAME AS positionName -- 직위명 FROM COMPETENCY_REPORT cr JOIN CYCLE cc ON cc.CYCLE_ID = cr.CYCLE_ID JOIN EMPLOYEE emp ON cr.EMP_ID = emp.EMP_ID JOIN DEPARTMENT dp ON emp.DEPT_ID = dp.DEPT_ID JOIN COMPANY_POSITION cmp ON emp.POSITION_ID = cmp.COM_POSITION_ID WHERE cr.IS_DELETED = 'N' AND emp.TYPE = 'WORKING' -- 재직 중 AND dp.DEPT_ID = #{deptId}
AND cc.YEAR = #{year}
AND cc.QUARTER = #{quarter}
AND cmp.COM_POSITION_ID = #{comPositionId}
AND emp.EMPLOYEE_NO LIKE CONCAT('%', #{employeeNo}, '%')
AND emp.NAME LIKE CONCAT('%', #{employeeName}, '%')
ORDER BY cc.YEAR DESC, cc.QUARTER DESC, cr.CREATED_AT DESC

SELECT cc.CYCLE_NAME , cc.YEAR , cc.QUARTER , DATE_FORMAT(cr.CREATED_AT, '%Y-%m-%d') AS createdAt , cr.COMPETENCY_REPORT_ID FROM CYCLE cc JOIN COMPETENCY_REPORT cr USING (CYCLE_ID) WHERE cr.EMP_ID = #{userId}
AND cc.YEAR = #{request.year}
ORDER BY cc.YEAR DESC, cc.QUARTER DESC, cr.CREATED_AT DESC
