mapper.select:
SELECT ct.CONTENT_ID AS contentId , ct.LEARNING_TIME AS learningTime , ct.LEVEL AS LEVEL , levelCode.CUSTOM_NAME AS levelName , ct.RESOURCE_PATH AS resourcePath , ct.TITLE AS title , typeCode.CUSTOM_NAME AS typeName , tg.TAG_ID AS tagId , tg.TAG_NAME AS tagName , ct.IS_DELETED AS isDeleted FROM CONTENT ct JOIN CONTENT_TAG ctt ON ct.CONTENT_ID = ctt.CONTENT_ID JOIN COMPETENCY_REPORT_CUSTOM_CODE typeCode ON ct.TYPE = typeCode.CUSTOM_CODE_ID AND typeCode.IS_DELETED = 'N' JOIN COMPETENCY_REPORT_CUSTOM_CODE levelCode ON ct.LEVEL = levelCode.CUSTOM_CODE_ID AND levelCode.IS_DELETED = 'N' JOIN tag tg ON ctt.tag_id = tg.TAG_ID AND tg.IS_DELETED = 'N' WHERE ct.IS_DELETED = 'N' AND ct.COMPANY_ID = #{comId}
AND ct.LEVEL = #{level}
AND ct.TITLE LIKE CONCAT('%', #{title}, '%')
AND ct.TYPE = #{type}
AND ct.LEARNING_TIME <= #{learningTime}
AND tg.TAG_NAME LIKE CONCAT('%', #{tag}, '%')
