mapper.select:
SELECT
    n.notice_id AS id,
    n.title,
    COALESCE(c.name, '미분류') AS categoryName,
    n.created_at AS createdAt
FROM notice n
LEFT JOIN notice_category c ON n.category_id = c.category_id AND c.is_deleted = 'N'
WHERE n.com_id = #{cond.companyId}
AND n.is_deleted = false


    AND n.category_id = #{cond.categoryId}



    AND (
    n.title LIKE #{cond.keyword.like()}
    OR n.content LIKE #{cond.keyword.like()}
    )


ORDER BY

     n.created_at 
     n.created_at 


     ASC 
     DESC 


LIMIT #{size}
OFFSET #{offset}

SELECT COUNT(*)
FROM notice n
WHERE n.com_id = #{cond.companyId}
AND n.is_deleted = false
AND n.status = 'PUBLISHED'


    AND n.category_id = #{cond.categoryId}



    AND (
    n.title LIKE #{cond.keyword.like()}
    OR n.content LIKE #{cond.keyword.like()}
    )
